class Solution:
    def longestValidParentheses(self, s: str) -> int:
        if len(s) < 2: return 0

        dp_dict = {}
        for idx in range(len(s)):
            if s[idx:idx + 2] == '()': dp_dict[idx] = 2

        enough_is_enough_counter = 0
        candidate = 0
        while enough_is_enough_counter < 11:

            if len(dp_dict) > 0:
                if candidate < max(dp_dict.values()):
                    enough_is_enough_counter = 0
                    candidate = max(dp_dict.values())
            enough_is_enough_counter += 1

            # looping each already known string to see if longer one exists
            for idx in list(dp_dict):
                if (idx >= 1) and (idx + dp_dict[idx] <= len(s) - 1):
                    if (s[idx - 1] == '(') and (s[idx + dp_dict[idx]] == ')'):
                        if idx - 1 in dp_dict:
                            if dp_dict[idx - 1] < dp_dict[idx] + 2: dp_dict[idx - 1] = dp_dict[idx] + 2
                        else:
                            dp_dict[idx - 1] = dp_dict[idx] + 2
                        continue
                if idx >= 2:
                    if s[idx - 2:idx] == '()':
                        if idx - 2 in dp_dict:
                            if dp_dict[idx - 2] < dp_dict[idx] + 2: dp_dict[idx - 2] = dp_dict[idx] + 2
                        else:
                            dp_dict[idx - 2] = dp_dict[idx] + 2
                        continue
                if idx + dp_dict[idx] <= len(s) - 2:
                    if s[idx + dp_dict[idx]:idx + dp_dict[idx] + 2] == '()':
                        if dp_dict[idx] < dp_dict[idx] + 2: dp_dict[idx] = dp_dict[idx] + 2
                        continue

            # checking if two strings are next to each other
            for idx in list(sorted(dp_dict)):
                if idx + dp_dict[idx] in dp_dict:
                    dp_dict[idx] = dp_dict[idx] + dp_dict[idx + dp_dict[idx]]

        if len(dp_dict) == 0: return 0
        return max(dp_dict.values())


